import React, { useState, useEffect, Fragment } from "react";
import Navbar from "../../Components/Navbar";
import SubjectsList from "../../Components/SubjectsList";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useLocation } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import "./Subjects.css";

function Subjects() {
  const [data, setData] = useState([]);
  const location = useLocation();

  useEffect(() => {
    axios
      .get(`https://localhost:7151/api/Subject/GetSubject`)
      .then((result) => {
        setData(result.data);
      })
      .catch((error) => {
        toast.error(error.toString());
      });
  }, [location.state.studentId.studentId]);
  
  const handleCreate = () => {
    const url = "https://localhost:7151/api/Subject/PostSubject";
    const newData1 = {
      firstName: firstName,
      lastName: lastName,
      age: age,
      budget: Budget,
    };

    if (isInputBlank(firstName) || isInputBlank(lastName) || isInputBlank(age))
      alert("One of the input is consist only from white spaces!");
    else if (hasSpecialCharacters(firstName) || hasSpecialCharacters(lastName))
      alert("First name or last name has special characters.");
    else if (isNumber(firstName) || isNumber(lastName))
      alert("Names can not be numbers");
    else if (nameContainsNumbers(firstName) || nameContainsNumbers(lastName))
      alert("Names can not contain numbers");
    else if (
      valueContainsWhiteSpaces(firstName) === false ||
      valueContainsWhiteSpaces(lastName) === false ||
      valueContainsWhiteSpaces(age) === false
    )
      alert("Name is not a correct value");
    else if (isInputOnlyNumbers(age) === false) alert("Age is not a number");
    else if (age < 16 || age > 100) alert("Age should be between 16 and 100");
    else if (age.toString().indexOf(".") !== -1)
      alert("Age is accepted only as an integer!");
    else {
      axios
        .post(url, newData1)
        .then((result) => {
          toast.success("Student successfully added.");
          getData();
        })
        .catch((error) => {
          toast.error(error.toString());
        });
      setFirstName("");
      setLastName("");
      setAge("");
      setBudget("NO");
    }
  };








  return (
    <>
      <Fragment>
        <ToastContainer />
        <Navbar
          text={
            location.state.studentFirstName.firstName +
            " " +
            location.state.studentLastName.lastName
          }
        />

        <Container className="container">
          <Row>
            <Col>
              <h1 className="h1Label">Subject Name</h1>
              <Form.Control
                className="inputName"
                type="text"
                placeholder="Subject Name"
              />
            </Col>
            <Col>
              <h1 className="h1Label">Mark</h1>
              <Form.Control
                className="inputName"
                type="text"
                placeholder="Mark"
              />
            </Col>
            <Col>
              <Button variant="success" className="addButton">
                Add New Subject
              </Button>
            </Col>
          </Row>
        </Container>

        <SubjectsList subjectsData={data}></SubjectsList>
      </Fragment>
    </>
  );
}

export default Subjects;
