import React from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const SubjectsCard = ({ id, subjectName, mark }) => {
  function selectImg(subjectName) {
    if (subjectName === "teologie")
      return "https://www.theosthinktank.co.uk/cmsfiles/theology.jpg";
    else return "https://clipartcraft.com/images/math-clipart-word-5.png";
  }

  const handleEdit = (id) => {
    handleShow();
    axios
      .get(`https://localhost:7151/api/Student/GetStudent/${id}`)
      .then((result) => {
        setEditFirstName(result.data.firstName);
        setEditLastName(result.data.lastName);
        setEditAge(result.data.age);
        setEditBudget(result.data.budget);
        setEditID(id);
      })
      .catch((error) => {
        toast.error(error.toString());
      });
  };

  const handleUpdate = () => {
    const url = `https://localhost:7151/api/Student/PutStudent/${editID}`;
    const newData = {
      subjectId: editID,
      subjectName: editFirstName,
      lastName: editLastName,
      age: editAge,
      budget: editBudget,
    };

    if (Mark < 1 || Mark > 10) alert("Mark should be in interval [1,10]");
    else {
      axios
        .put(url, newData)
        .then(() => {
          toast.success("Subject successfully updated.");
          getData();
          handleClose();
        })
        .catch((error) => {
          toast.error(error.toString());
        });
    }
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this subject?")) {
      axios
        .delete(`https://localhost:7151/api/Subject/DeleteEmployee/${id}`)
        .then((result) => {
          if (result.status === 200) {
            toast.success("Subject has been delete.");
          }
        })
        .catch((error) => {
          toast.error(error.toString());
        });
    }
  };

  return (
    <div className="SubjectsCardDiv">
      <Card>
        <Card.Img
          className="SubjectsCardImg"
          variant="top"
          src={selectImg(subjectName)}
          alt={"image " + subjectName}
        />
        <Card.Body>
          <Card.Title>
            <p>Subject name: {subjectName}</p>
          </Card.Title>
          <Card.Text>
            <p>Mark: {mark}</p>
          </Card.Text>
          <Button variant="primary">Edit</Button>
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          <Button variant="danger" onClick={() => handleDelete(id)}>
            Delete Subject
          </Button>
        </Card.Body>
      </Card>
    </div>
  );
};

export default SubjectsCard;
