import React, { useState, useEffect, Fragment } from "react";
import Navbar from "../../Components/Navbar";
import SubjectsList from "../../Components/SubjectsList";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useLocation } from "react-router-dom";

function Subjects() {
  const [data, setData] = useState([]);

  const location = useLocation();

  const getData = (id) => {
     axios
      .get(`https://localhost:7151/api/Subject/${id}`)
      .then((result) => {
        setEditFirstName(result.data.firstName);
        setEditLastName(result.data.lastName);
        setEditAge(result.data.age);
        setEditBudget(result.data.budget);
        setEditID(id);
      })
      .catch((error) => {
        toast.error(error.toString());
      });
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Fragment>
        <ToastContainer />
        <Navbar
          text={
            location.state.studentFirstName.firstName +
            " " +
            location.state.studentLastName.lastName
          }
        />

        <SubjectsList subjectsData={data}></SubjectsList>
      </Fragment>
    </>
  );
}

export default Subjects;
