import React, { useState, useEffect, Fragment } from "react";
import Navbar from "../../Components/Navbar";
import SubjectsList from "../../Components/SubjectsList";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useLocation } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import "./Subjects.css";

function Subjects() {
  const [data, setData] = useState([]);
  const location = useLocation();

  useEffect(() => {
    axios
      .get(`https://localhost:7151/api/Subject/GetSubject`)
      .then((result) => {
        setData(result.data);
      })
      .catch((error) => {
        toast.error(error.toString());
      });
  }, [location.state.studentId.studentId]);
  
  







  
  return (
    <>
      <Fragment>
        <ToastContainer />
        <Navbar
          text={
            location.state.studentFirstName.firstName +
            " " +
            location.state.studentLastName.lastName
          }
        />

        <Container className="container">
          <Row>
            <Col>
              <h1 className="h1Label">Subject Name</h1>
              <Form.Control
                className="inputName"
                type="text"
                placeholder="Subject Name"
              />
            </Col>
            <Col>
              <h1 className="h1Label">Mark</h1>
              <Form.Control
                className="inputName"
                type="text"
                placeholder="Mark"
              />
            </Col>
            <Col>
              <Button variant="success" className="addButton">
                Add New Subject
              </Button>
            </Col>
          </Row>
        </Container>

        <SubjectsList subjectsData={data}></SubjectsList>
      </Fragment>
    </>
  );
}

export default Subjects;
