import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Modal from "react-bootstrap/Modal";
import axios from "axios";
import Form from "react-bootstrap/Form";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const SubjectsCard = ({ id, subjectNameParameter, mark, studentId }) => {
  const [subjectName, setSubjectName] = useState("");
  const [Mark, setMark] = useState("");
  const [editSubjectName, setEditSubjectName] = useState("");
  const [editMark, setEditMark] = useState("");
  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);

  function selectImg(subjectNameParameter) {
    if (subjectNameParameter === "teologie")
      return "https://www.theosthinktank.co.uk/cmsfiles/theology.jpg";
    else return "https://clipartcraft.com/images/math-clipart-word-5.png";
  }

  const handleEdit = (id) => {
    handleShow();
    axios
      .get(`https://localhost:7151/api/Subject/GetSubject/${id}`)
      .then((result) => {
        setSubjectName(result.data.subjectName);
        setMark(result.data.mark);
      })
      .catch((error) => {
        toast.error(error.toString());
      });
  };

  const handleUpdate = (editID) => {
    const url = `https://localhost:7151/api/Subject/PutSubject/${editID}`;
    const newData = {
      subjectId: editID,
      subjectName: editSubjectName,
      mark: editMark,
      studentId: { studentId },
    };

    if (Mark < 1 || Mark > 10) alert("Mark should be in interval [1,10]");
    else {
      axios
        .put(url, newData)
        .then(() => {
          toast.success("Subject successfully updated.");
          setShow(false);
          setEditSubjectName("");
          setEditMark("");
        })
        .catch((error) => {
          toast.error(error.toString());
        });
    }
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this subject?")) {
      axios
        .delete(`https://localhost:7151/api/Subject/DeleteEmployee/${id}`)
        .then((result) => {
          if (result.status === 200) {
            toast.success("Subject has been delete.");
          }
        })
        .catch((error) => {
          toast.error(error.toString());
        });
    }
  };

  return (
    <>
      <div className="SubjectsCardDiv">
        <Card>
          <Card.Img
            className="SubjectsCardImg"
            variant="top"
            src={selectImg(subjectNameParameter)}
            alt={"image " + subjectNameParameter}
          />
          <Card.Body>
            <Card.Title>
              <p>Subject name: {subjectNameParameter}</p>
            </Card.Title>
            <Card.Text>
              <p>Mark: {mark}</p>
            </Card.Text>
            <Button variant="primary" onClick={() => handleEdit(id)}>
              Edit
            </Button>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <Button variant="danger" onClick={() => handleDelete(id)}>
              Delete Subject
            </Button>
          </Card.Body>
        </Card>
      </div>



      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Edit subject data</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Container>
            <Row>
              <input
                className="editInput"
                type="text"
                placeholder="Subject Name"
                value={subjectName}
                onChange={(e) => setEditFirstName(e.target.value)}
              />
            </Row>
            <Row>
              <input
                className="editInput"
                type="text"
                placeholder="Mark"
                value={editLastName}
                onChange={(e) => setEditLastName(e.target.value)}
              />
            </Row>
          </Container>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleUpdate}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default SubjectsCard;
