import React, { useState, useEffect, Fragment } from "react";
import Navbar from "../../Components/Navbar";
import SubjectsList from "../../Components/SubjectsList";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useLocation } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import "./Subjects.css";

function Subjects() {
  const [data, setData] = useState([]);
  const [subjectName, setSubjectName] = useState("");
  const [Mark, setMark] = useState("");
  const location = useLocation();

  useEffect(() => {
    getData();
  });

  const getData = () => {
    axios
      .get(`https://localhost:7151/api/Subject/GetSubject`)
      .then((result) => {
        setData(result.data);
      })
      .catch((error) => {
        toast.error(error.toString());
      });
  };

  const handleCreate = () => {
    const url = "https://localhost:7151/api/Subject/PostSubject";
    const newData = {
      subjectName: subjectName,
      mark: Mark,
      studentId
    };

    axios
      .post(url, newData)
      .then((result) => {
        toast.success("Student successfully added.");
        getData();
      })
      .catch((error) => {
        toast.error(error.toString());
      });
    setSubjectName("");
    setMark("");
  };

  return (
    <>
      <Fragment>
        <ToastContainer />
        <Navbar
          text={
            location.state.studentFirstName.firstName +
            " " +
            location.state.studentLastName.lastName
          }
        />

        <Container className="container">
          <Row>
            <Col className="subjectColumn">
              <h1 className="h1Label">Subject Name</h1>
              {/* <div className="dropDownDiv"> <DropDown className="inputName"></DropDown></div> */}
              <div className="formDiv">
                <Form.Control
                  className="inputName"
                  type="text"
                  placeholder="Subject Name"
                  value={subjectName}
                  onChange={(e) => setSubjectName(e.target.value)}
                />
              </div>
            </Col>
            <Col className="subjectColumn">
              <h1 className="h1Label">Mark</h1>
              <div className="formDiv">
                {/* <div className="dropDownDiv"> <DropDown className="inputName"></DropDown></div> */}
                <Form.Control
                  className="mark"
                  type="text"
                  placeholder="Mark"
                  value={Mark}
                  onChange={(e) => setMark(e.target.value)}
                />
              </div>
            </Col>
            <Col className="subjectColumn">
              <Button
                variant="success"
                className="addButton"
                onClick={() => handleCreate()}
              >
                Add New Subject
              </Button>
            </Col>
          </Row>
        </Container>

        <SubjectsList subjectsData={data}></SubjectsList>
      </Fragment>
    </>
  );
}

export default Subjects;
